mapper.select:
SELECT admin_id, account, password, name, registry_time
FROM admin
WHERE account = #{account}

SELECT admin_id, account, password, name, registry_time
FROM admin
WHERE admin_id = #{adminId}

mapper.update:
UPDATE admin
SET
password = #{password}
WHERE admin_id = #{adminId}

UPDATE admin
SET
name = #{name}
WHERE admin_id = #{adminId}
