mapper.select:
SELECT score
FROM experiment_selected_list
WHERE student_id = #{studentId} AND experiment_id = #{experimentId}

mapper.update:
UPDATE experiment_selected_list
SET score = #{score}
WHERE student_id = #{studentId} AND experiment_id = #{experimentId}

mapper.delete:
DELETE FROM experiment_selected_list
WHERE
experiment_id = #{experimentId} AND student_id = #{studentId}
